text:
Setting the Environment

Codelibrary(brandr)
library(dplyr)
library(ggplot2)
library(here)
library(janitor)
library(patchwork)
library(readr)
library(stats)


Figure 10.1.1

Codedata &lt;-
  here::here(
    "data",
    "business-investor-model-10-4-2-figure-10-1-1-table.csv"
  ) |&gt;
  readr::read_csv(
    skip = 6,
    col_types = readr::cols(.default = "c")
  ) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(
    run_number,
    sensing_radius,
    mean_wealth_of_investors,
    standard_deviation_wealth_of_investors
  ) |&gt;
  dplyr::mutate(
    dplyr::across(
      .cols = dplyr::everything(),
      .fns = as.numeric
    )
  ) |&gt;
  dplyr::summarize(
    mean = mean(mean_wealth_of_investors, na.rm = TRUE),
    sd = mean(standard_deviation_wealth_of_investors, na.rm = TRUE),
    .by = sensing_radius
  )



Codedata







Codedata |&gt;
  ggplot2::ggplot() +
  ggplot2::geom_point(
    mapping = ggplot2::aes(x = sensing_radius, y = mean),
    shape = 19,
  ) +
  ggplot2::geom_point(
    mapping = ggplot2::aes(x = sensing_radius, y = sd),
    shape = 8,
  ) +
  ggplot2::scale_x_continuous(breaks = seq(0, 10, 2)) +
  ggplot2::scale_y_continuous(
    breaks = seq(0, 600000, 100000),
    labels = ~ prettyNum(.x, big.mark = ",")
  ) +
  ggplot2::labs(
    x = "Sensing radius",
    y = "Mean final investor wealth"
  )







Figure 10.1.2

Codedata &lt;-
  here::here(
    "data",
    "business-investor-model-10-4-2-figure-10-1-2-table.csv"
  ) |&gt;
  readr::read_csv(
    skip = 6,
    col_types = readr::cols(.default = "c")
  ) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(
    run_number,
    step,
    sensing_radius,
    mean_wealth_of_investors
  ) |&gt;
  dplyr::mutate(
    dplyr::across(
      .cols = dplyr::everything(),
      .fns = as.numeric
    )
  ) |&gt;
  dplyr::mutate(
    sensing_radius = factor(sensing_radius, ordered = TRUE)
  ) |&gt;

  dplyr::summarize(
    mean = mean(mean_wealth_of_investors, na.rm = TRUE),
    .by = c("step", "sensing_radius")
  )



Codedata







Codedata |&gt;
  ggplot2::ggplot(ggplot2::aes(x = step, y = mean, color = sensing_radius)) +
  ggplot2::geom_line() +
  ggplot2::scale_x_continuous(breaks = seq(0, 50, 10)) +
  ggplot2::scale_y_continuous(
    breaks = seq(0, 700000, 100000),
    labels = ~ prettyNum(.x, big.mark = ",")
  ) +
  ggplot2::labs(
    x = "Year",
    y = "Mean investor wealth",
    color = "Sensing radius"
  ) +
  brandr::scale_color_brand_d()

Model 11.5
Figure 12.1

Codedata &lt;-
  here::here(
    "data",
    "business-investor-model-11-5-figure-12-1-table.csv"
  ) |&gt;
  readr::read_csv(
    skip = 6,
    col_types = readr::cols(.default = "c")
  ) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(
    run_number,
    decision_time_horizon,
    mean_wealth_of_investors,
    mean_profit_of_turtles_on_patches,
    mean_risk_of_failing_of_turtles_on_patches,
    total_business_failures
  ) |&gt;
  dplyr::mutate(
    dplyr::across(
      .cols = dplyr::everything(),
      .fns = as.numeric
    )
  ) |&gt;
  dplyr::mutate(
    decision_time_horizon = factor(decision_time_horizon, ordered = TRUE)
  )



Codedata







Codeplot_1 &lt;-
  data |&gt;
  dplyr::summarize(
    mean = mean(mean_wealth_of_investors, na.rm = TRUE),
    sd = stats::sd(mean_wealth_of_investors, na.rm = TRUE),
    .by = decision_time_horizon
  ) |&gt;
  ggplot2::ggplot(
    ggplot2::aes(
      x = decision_time_horizon,
      y = mean,
      ymin = mean - sd,
      ymax = mean + sd
    )
  ) +
  ggplot2::geom_point(shape = 19) +
  ggplot2::geom_errorbar(linewidth = 0.2) +
  ggplot2::scale_x_discrete(breaks = seq(0, 25, 5)) +
  ggplot2::scale_y_continuous(
    breaks = seq(0, 400000, 100000),
    labels = ~ prettyNum(.x, big.mark = ","),
    limits = c(0, 400000)
  ) +
  ggplot2::labs(
    x = ggplot2::element_blank(),
    y = "Mean final investor wealth"
  )



Codeplot_2 &lt;-
  data |&gt;
  dplyr::summarize(
    mean = mean(total_business_failures, na.rm = TRUE),
    sd = stats::sd(total_business_failures, na.rm = TRUE),
    .by = decision_time_horizon
  ) |&gt;
  ggplot2::ggplot(
    ggplot2::aes(
      x = decision_time_horizon,
      y = mean,
      ymin = mean - sd,
      ymax = mean + sd
    )
  ) +
  ggplot2::geom_point(shape = 19) +
  ggplot2::geom_errorbar(linewidth = 0.2) +
  ggplot2::scale_x_discrete(breaks = seq(0, 25, 5)) +
  ggplot2::scale_y_continuous(limits = c(0, 200)) +
  ggplot2::labs(
    x = ggplot2::element_blank(),
    y = "Number of failures"
  )



Codeplot_3 &lt;-
  data |&gt;
  dplyr::summarize(
    mean = mean(mean_profit_of_turtles_on_patches, na.rm = TRUE),
    sd = stats::sd(mean_profit_of_turtles_on_patches, na.rm = TRUE),
    .by = decision_time_horizon
  ) |&gt;
  ggplot2::ggplot(
    ggplot2::aes(
      x = decision_time_horizon,
      y = mean,
      ymin = mean - sd,
      ymax = mean + sd
    )
  ) +
  ggplot2::geom_point(shape = 19) +
  ggplot2::geom_errorbar(linewidth = 0.2) +
  ggplot2::scale_x_discrete(breaks = seq(0, 25, 5)) +
  ggplot2::scale_y_continuous(
    breaks = seq(0, 12500, 2500),
    labels = ~ prettyNum(.x, big.mark = ","),
    limits = c(0, 12500)
  ) +
  ggplot2::labs(
    x = "Time horizon (years)",
    y = "Mean annual profit"
  )



Codeplot_4 &lt;-
  data |&gt;
  dplyr::summarize(
    mean = mean(
      mean_risk_of_failing_of_turtles_on_patches,
      na.rm = TRUE
    ),
    sd = stats::sd(
      mean_risk_of_failing_of_turtles_on_patches,
      na.rm = TRUE
    ),
    .by = decision_time_horizon
  ) |&gt;
  ggplot2::ggplot(
    ggplot2::aes(
      x = decision_time_horizon,
      y = mean,
      ymin = mean - sd,
      ymax = mean + sd
    )
  ) +
  ggplot2::geom_point(shape = 19) +
  ggplot2::geom_errorbar(linewidth = 0.2) +
  ggplot2::scale_x_discrete(breaks = seq(0, 25, 5)) +
  ggplot2::scale_y_continuous(
    breaks = seq(0, 0.03, 0.01),
    labels = ~ prettyNum(.x, big.mark = ","),
    limits = c(0, 0.03)
  ) +
  ggplot2::labs(
    x = "Time horizon (years)",
    y = "Mean annual risk"
  )



Codepatchwork::wrap_plots(
  plot_1 + plot_2 + plot_3 + plot_4,
  ncol = 2,
  axes = "collect_x"
)







Figure 12.2

Codedata &lt;-
  here::here(
    "data",
    "business-investor-model-11-5-figure-12-2-table.csv"
  ) |&gt;
  readr::read_csv(
    skip = 6,
    col_types = readr::cols(.default = "c")
  ) |&gt;
  janitor::clean_names() |&gt;
  dplyr::select(
    run_number,
    step,
    decision_time_horizon,
    mean_risk_of_failing_of_turtles_on_patches
  ) |&gt;
  dplyr::mutate(
    dplyr::across(
      .cols = dplyr::everything(),
      .fns = as.numeric
    )
  ) |&gt;
  dplyr::mutate(
    decision_time_horizon = factor(decision_time_horizon, ordered = TRUE)
  ) |&gt;

  dplyr::summarize(
    mean = mean(mean_risk_of_failing_of_turtles_on_patches, na.rm = TRUE),
    .by = c("step", "decision_time_horizon")
  )



Codedata







Codedata |&gt;
  ggplot2::ggplot(
    ggplot2::aes(x = step, y = mean, color = decision_time_horizon)
  ) +
  ggplot2::geom_line() +
  ggplot2::scale_x_continuous(breaks = seq(0, 50, 10)) +
  ggplot2::scale_y_continuous(limits = c(0, 0.06)) +
  ggplot2::labs(
    x = "Time step (years)",
    y = "Mean annual risk",
    color = "Time horizon (years)"
  ) +
  brandr::scale_color_brand_d()
